import React,{useState, useEffect} from 'react';
import {Link} from 'react-router-dom'
import './App.css';
import { List, Avatar, Icon} from 'antd';
import Nav from './Nav'

function ScreenSource() {

  const [sourceList, setSourceList] = useState([])
  const [selectedLang, setselectedLang] = useState('fr')



  useEffect(() => {
    const APIResultsLoading = async() => {
      if (selectedLang == 'en'){}
      const data = await fetch('https://newsapi.org/v2/sources?language=fr&country=fr&apiKey=2883e07c86dd475cac2332c62fc8a276')
      const body = await data.json()
      setSourceList(body.sources)
    }

    APIResultsLoading()
  }, [])

  return (
    <div>
        <Nav/>

       <div className="Banner">

       <div>

          <Avatar   class="ant-avatar" size={64} src={`/images/france.jpg`} />
          <Avatar class="ant-avatar" size={64} src={`/images/eng.jpg`} />

       </div>

      </div>
       <div className="HomeThemes">

              <List
                  itemLayout="horizontal"
                  dataSource={sourceList}
                  renderItem={source => (
                    <List.Item>
                      <List.Item.Meta
                        avatar={<Avatar src={`/images/${source.category}.png`} />}
                        title={<Link to={`/screenarticlesbysource/${source.id}`}>{source.name}</Link>}
                        description={source.description}
                      />
                    </List.Item>
                  )}
                />


          </div>

      </div>
  );
}

export default ScreenSource;
